SELECT start.arrival_time DepartureTime, Stop.arrival_time ArrivalTime, Start.block_id FROM
(SELECT T.trip_id, ST.arrival_time, T.block_id FROM stop_times_{4} ST JOIN trips_{4} T ON ST.trip_id = T.trip_id WHERE ST.stop_id = {0} and T.direction_id = {2} and T.service_id in (select service_id from calendar_bus C where days & {1})) Start
JOIN (SELECT T.trip_id, T.direction_id, T.service_id, ST.arrival_time FROM stop_times_{4} ST JOIN trips_{4} T ON ST.trip_id = T.trip_id WHERE stop_id = {3} and T.direction_id = {2} and T.service_id in (select service_id from calendar_bus C where days & {1})) Stop ON start.trip_id = stop.trip_id group by start.arrival_time, Stop.arrival_time ORDER BY DepartureTime;

SELECT start.arrival_time DepartureTime, Stop.arrival_time ArrivalTime, start.block_id FROM
(SELECT T.trip_id,T.route_id, ST.arrival_time, T.block_id FROM stop_times_rail ST JOIN trips_rail T ON ST.trip_id = T.trip_id WHERE ST.stop_id = {0} and T.direction_id = {2} and T.service_id in (select service_id from calendar_rail C where days & {1})) Start
JOIN (SELECT T.trip_id, T.direction_id, T.service_id, ST.arrival_time FROM stop_times_rail ST JOIN trips_rail T ON ST.trip_id = T.trip_id WHERE stop_id = {3} and T.direction_id = {2} and T.service_id in (select service_id from calendar_rail C where days & {1})) Stop ON start.trip_id = stop.trip_id group by start.arrival_time, stop.arrival_time;
